ex:Product001 a schema:Product ;
              schema:name "Super Power Drill"@en ;
              schema:description "High-performance drill..."@en ;
              schema:color "red"@en, "black"@en ; #

ex:SummerFest25 ex:organizer [
    ex:email <mailto:komitee@example.org> ;
    ex:phone <tel:+49-123...> ;
    ex:contactPoint [               # 
         a foaf:Person ;
         foaf:name "Ms. Meier"
    ]

 ;
              ex:location ex:AachenCity .

ex:AachenCity rdfs:label "Aachen" .

ex:DM_Lecture ex:heldAt ex:RWTHAachen ;

SELECT ?studentName
WHERE {
  ?student a :Student ;
           :name ?studentName .
  #  1 Logic 
  FILTER NOT EXISTS { 
    ?student :attends ?lecture . 
    ?lecture :title "Logic" 
  }
  #  2 F 
  FILTER (!STRSTARTS(?studentName, "F"))

SELECT ?name ?title
WHERE {
  ?student a :Student ; :name ?name .
  ?lecture a :Lecture ; :title ?title ;
           :prerequisiteLecture ?anyPrereq . # 

  #  3
  MINUS { ?student :attends ?lecture }

  #  2
  FILTER NOT EXISTS {
    ?lecture :prerequisiteLecture ?prereq .
    MINUS { ?student :attends ?prereq }
  }

SELECT ?result WHERE {
  ?holiday a ex:ReligiousHoliday .
  { ?holiday ex:gregorianDate ?result . }
  UNION
  { ?holiday ex:otherDate ?result . }

WHERE{
    ?sensor a ssn:Sensor;
            rdfs:label ?name.
    ?obs a sosa:Observation;
         sosa:madeBySensor ?sensor.
}

SELECT ?name (?value AS ?max_air_pressure)
WHERE {
  ?obs a sosa:Observation ;
       sosa:madeBySensor ?sensor ;
       sosa:observedProperty ex:AirPressure ;
       sosa:hasSimpleResult ?value .
  ?sensor rdfs:label ?name .
}
ORDER BY DESC(?value)

SELECT ?property ?station (AVG(?value) AS ?average)
WHERE {
    ?obs a sosa:Observation ;
         sosa:observedProperty ?property ;
         sosa:madeBySensor ?sensor ;
         sosa:hasSimpleResult ?value .
    ?station sosa:hosts ?sensor .
}

SELECT ?property (AVG(?value) AS ?average)
WHERE {
  ?obs a sosa:Observation ;
       sosa:observedProperty ?property ;
       sosa:hasSimpleResult ?value .
  FILTER NOT EXISTS { ?obs ex:shouldBeIgnoredIn "2e" }
}

SELECT ?name WHERE {
  { ?city :name ?name . ?city a :City }
  UNION
  { ?city rdfs:label ?name . ?city a :City }

SELECT ?person ?name WHERE {
  {
    ?person a :Student ;       # First block: find students
            :name ?name .
  }
  UNION
  {
    ?person a :Professor ;     # Second block: find professors
            :name ?name .
  }

SELECT ?name ?zip WHERE {
   ?city :name ?name .        # Mandatory Pattern
   OPTIONAL {
      ?city :zip ?zip         # Optional Pattern
   }
